package org.opentutorials.javatutorials.loop_iteration;

public class breakloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	     for (int i = 0; i < 10; i++) {
	            if (i == 5)
	                break;
	            System.out.println("Coding Everybody " + i);
	        }
	}

}
